// Format currency for KPI display
function formatCurrency(amount) {
  if (!amount) return '0 kr';
  return new Intl.NumberFormat('da-DK', { 
    style: 'decimal',
    minimumFractionDigits: 0,
    maximumFractionDigits: 0 
  }).format(amount) + ' kr';
}

