// Update section field
function updateSectionField(sectionId, field, value) {
  const page = getCurrentCMSPage();
  if (!page) return;

  const section = page.sections.find(s => s.id === sectionId);
  if (section) {
    section[field] = value;
    page.updatedAt = new Date().toISOString();
    markCMSChanged();
  }
}

